export class CalcIVS {

    private error?: string;
    private ivs:[number,number,number,number,number,number] = [0,0,0,0,0,0];

    constructor(private species: number,
        private level: number,
        private nature: number,
        private stats: [number, number,number,number,number,number],
        private evs: [number, number,number,number,number,number] = [0,0,0,0,0,0]) {

    }
    
    private calc_stat(species: number, stat: Stat, stativ: number, statev: number, pokelvl: number, nature: number): number { //Calculates Pokemon stats
        var basestat = get_base(species, stat);
        var bonus = natures[nature][(stat-1)];
        if(!basestat) return -1;

        if (stat == Stat.HP) { //HP uses a different formula
            if (species == 292) { return 1; } //Shedinja Case
            return (Math.floor(((basestat*2 + (stativ/1) + Math.floor(statev/4))*pokelvl)/100)) + (pokelvl/1) + 10;
        }
        return Math.floor((Math.floor(((basestat*2 + (stativ/1) + Math.floor(statev/4))*pokelvl)/100) + 5)*(bonus||1));
    }

    private get_closest_iv(species: number, stat: number, stat_calculating: Stat, statev: number, pokelvl: number, nature: number): number {
        var last_closest_diff = 9999999, last_closest_iv = 0;
        var i = 0;
        while(i < 32) { //when diff is growing again... we skipped -> return right away
            var temp_stat = this.calc_stat(species, stat_calculating, i, statev, pokelvl, nature);
            var diff = Math.abs(stat - temp_stat);
            if(diff < last_closest_diff) {
                last_closest_diff = diff;
                last_closest_iv = i;
            }
            i++;
        }
        return last_closest_iv;
    }

	private validate_iv(species: number, pstat: number, stat: number, statev: number, pokelvl: number, nature: number) { // IV error handling
		var [min, max] = [0, 31].map(iv => this.calc_stat(species, pstat, iv, statev, pokelvl, nature));
		if (stat > max || stat < min) {
            this.error = `${species} lvl${pokelvl} odes not compute. ${stat} should be between ${min} and ${max}`;
			return false;
		}

		return true;
    }
    
    calc() {
        return [Stat.HP, Stat.ATK, Stat.DEF, Stat.SPATK, Stat.SPDEF, Stat.SPD]
        .map(stat => this.get_closest_iv(this.species, this.stats[stat], stat, this.evs[stat], this.level, this.nature));
    }
}